//转换日期格式：将数据库数据转化为年-月-日
function date_db2view($str)
{
    $temp = '';
    $date = explode('-', $str);
    $y    = $date[0];
    $m    = $date[1];
    $d    = $date[2];
    $temp = $y . '年' . $m . '月' . $d . '日';
    return $temp;
}
